import { createSlice } from "@reduxjs/toolkit";
import { addQuizToTopic } from "../features/quizzes/QuizzesSlice";

export const thunkActionCreator = (payload) => {
    return (dispatch) => {
        dispatch(addQuiz(payload));
        dispatch(addQuizToTopic(payload));
    };
};

export const quizzesSlice = createSlice({
    name: "quizzes",
    initialState: {
        quizzes: {}
    },
    reducers: {
        addQuiz: (state, action) => {
            state.topics[action.payload.id] = { ...action.payload, cardIds: [] };
        }
    },
    extraReducers: {
        [thunkActionCreator]: (state, action) => {
            state.quizzes[action.payload.id] = { ...action.payload, cardIds: [] };
        }
    }
});

export const { addQuiz,  } = quizzesSlice.actions;
export default quizzesSlice.reducer;
export const selectQuizzes = state => state.topics.topics;
